var RegionsSelector = {
  popupBox: '', //сам попап, в котором будут искаться остальные элементы
  triggerLink: '',


  /* переключалка выбора метро / района */
  bindChangeSelector: function() {
    this.popupBox.find('legend').find('a.map-switch').each(function(){
      $(this).bind('click', function(){
        if (!$(this).hasClass('selected')) {
          var to_show = $(this).attr('rel'),
              to_hide = $(this).siblings('.selected').attr('rel'),
              $box = $(this).closest('div.metromapbox');
              if ($box.length == 0) {
                $box = $(this).closest('div.roadbox');
              }                

          $box.find('tbody td.'+to_hide).hide();
          $box.find('tbody td.'+to_show).show();

          $(this).addClass('selected').siblings().removeClass('selected');
        }
        regionMap.setDistrictScrollerHeight();
        return false;
      });
    });
  },

  /**
   * Кнопка "Применить"
   */
  bindApplyButtonClick:function() {
    var that = this,
        $form = this.popupBox,
        $list = this.triggerLink.parent('div').find('.metroList');
           
    if ($list.size() == 0) {
      that.triggerLink.parent('div').find('a.select-regionnode:last').after('<ul class="metroList"/>');
      $list = that.triggerLink.parent('div').find('.metroList');
    }
    
    $form.find('.formButton').bind('click', function() {
      var $closeButton = $(this).siblings('.popupClose:first');
      if ($closeButton.length === 0) {
        $closeButton = $(this).closest('div.body').find('.popupClose:first');
      }

      $list.empty();
      var html = '';

      $form.find('ul.regionnode-list').each(function() {
        $(this).find(':checked').each(function() {
          var node = '<input type="hidden" value="' + $(this).val() + '" name="regionnode[]" />';
          node += $(this).parent().text();
          html += '<li>' + node + '<a href="#">&nbsp;</a></li>';
        });
      });

      $list.append(html);
      if($('#frontstreet').size()) {
        street_auto_complete.apply( $('#frontstreet') );
      }

      if ($list.find('li').length == 0) {
        $list.remove();
      } else {
        $list.find('a').bind('click', function() {
          $(this).closest('li').remove();
          //Обновление списка улиц
          if($('#frontstreet').size()) {
            street_auto_complete.apply( $('#frontstreet') );
          }
          
          that.triggerLink.closest('form').trigger('submit');
          if ($list.find('li').length == 0) {
            $list.remove();
          }
          return false;
        });
      }

      $closeButton.trigger('click');

      that.triggerLink.closest('form').trigger('submit');
      return false;
    });
  },

  /**
   * заполняется список выбранных регионов в фильтре поиска в боковой колонке
   */
  checkSelectedRegions: function(){
    var $form = this.popupBox,
        $list = this.triggerLink.parent('div').find('.metroList');
        
    $list.find('input').each(function(){
      var id = $(this).attr('value'),
          item = $form.find('input[value="'+id+'"]');

      if (/м\. /.test(item.attr('value'))) {
        $('#pp'+item.attr('id').match(/\d+/)[0]).trigger('click');
      } else {
        item.trigger('fuckup');
      }

    });
  },

  /**
   *
   */

  /**
   * вставляет в список над картой районов новый элемент
   * @param id - id региона
   * @param type (string) - тип (класс div, в список внутри которого нужно добавить узел)
   */
  appendNodeToList: function(id, type){
    var $box = $('div.metromapbox').find('div.selected-list-box').find('div.' + type);

    if ($box.find('ul').length == 0)
      $box.append('<ul/>');

    var $ul = $box.find('ul');

    $ul.append('<li>'
    + (function(){return $('#'+id).parent('label').length ? $('#'+id).parent('label').text() : $('#'+id).siblings('label').text();})()
    +'\u00A0<a href="#" rel="'+id+'"></a></li>');

    RegionsSelector.checkNodeListVisibility();
  },

  /**
   * показывает / скрывает список регионов над картой округов
   */
  checkNodeListVisibility: function(){
    var $box = $('div.metromapbox').find('div.selected-list-box');

    $box.children('div').each(function(){
      if (!$(this).find('li').length) {
        $(this).hide();
      } else {
        $(this).show();
        $box.parent().show();
      }
    });

    if (!$box.find('li').length) {
      $box.parent().hide();
    }

  },

  /**
   * Удалить округ из списка выбранных округов над картой
   * @param id - id округа (напр. region_vao)
   */
  removeNodeFromList: function(id){
    if (id) {
      var $li = $('div.metromapbox').find('a[rel="' + id +'"]').parent();

      if ($li.siblings().length == 0) {
        $li.parent().remove();
      }
      else {
        $li.remove();
      }

      this.checkNodeListVisibility();
    }
  },

  /**
   * развешиваем события по клику на крестик - удаление района из списка над картой
   */
  bindRemoveDistrictLinks: function(){
    $('div.metromapbox').find('div.selected-list-box li').find('a').live('click', function(){
      var id = $(this).attr('rel'),
          type = $(this).closest('div').attr('class');
      switch (type) {
        case 'districts':
          $('#'+id).trigger('fuckup');
        break;
        case 'metro':
          $('#pp'+id.match(/\d+/)[0]).trigger('click');
        break;
        case 'regions':
          $('#'+id).trigger('fuckup');
        break;
      }
      return false;
    });
  },

  bindClearButton: function(){
    $('div.metromapbox').find('a.popupReset').bind('click', function(){
      $('div.metromapbox').find('div.selected-list-box li').find('a').each(function(){$(this).trigger('click')});
      return false;
    });
  },

  init: function(){
    this.bindChangeSelector();
    this.bindApplyButtonClick();
    this.bindClearButton();
    this.bindRemoveDistrictLinks();
  }
};
